  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'username-email-password',
	title: 'Username / Email & Password'
};
export const metadata = {
  "id": "concepts/credentials/username-email-password",
  "title": "Username / Email & Password",
  "description": "The `password` method is the most commonly used form of authentication, it",
  "source": "@site/docs/concepts/credentials/username-email-password.mdx",
  "permalink": "/kratos/docs/next/concepts/credentials/username-email-password",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/docs/concepts/credentials/username-email-password.mdx",
