	id: 'verify-email-account-activation',
	title: 'Email and Phone Verification and Account Activation'
};
export const metadata = {
  "id": "self-service/flows/verify-email-account-activation",
  "title": "Email and Phone Verification and Account Activation",
  "description": "import useBaseUrl from '@docusaurus/useBaseUrl';",
