  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'user-logout',
	title: 'User Logout'
};
export const metadata = {
  "id": "version-v0.1/self-service/flows/user-logout",
  "title": "User Logout",
  "description": "ORY Kratos supports two logout flows:",
  "source": "@site/versioned_docs/version-v0.1/self-service/flows/user-logout.md",
  "permalink": "/kratos/docs/v0.1/self-service/flows/user-logout",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/versioned_docs/version-v0.1/self-service/flows/user-logout.md",
  "version": "v0.1",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "version-v0.1/docs",
  "previous": {
    "title": "User Login and User Registration",
    "permalink": "/kratos/docs/v0.1/self-service/flows/user-login-user-registration"
  },
  "next": {
    "title": "user-profile-management",
    "permalink": "/kratos/docs/v0.1/self-service/flows/user-profile-management"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Self-Service User Logout for Browser Applications',
  id: 'self-service-user-logout-for-browser-applications',
  children: []
}, {
  value: 'Self-Service User Logout for API Clients',
  id: 'self-service-user-logout-for-api-clients',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`ORY Kratos supports two logout flows:`}</p>
    <ul>
      <li parentName="ul">{`Browser-based (easy): This flow works for all applications running on top of a
browser. Websites, single-page apps, Cordova/Ionic, and so on.`}</li>
      <li parentName="ul">{`API-based (advanced): This flow works for native applications like iOS
(Swift), Android (Java), Microsoft (.NET), React Native, Electron, and others.`}</li>
    </ul>
    <h2 {...{
      "id": "self-service-user-logout-for-browser-applications"
    }}>{`Self-Service User Logout for Browser Applications`}</h2>
    <blockquote>
      <p parentName="blockquote">{`WARNING - This flow is currently vulnerable to CSRF attacks because anyone can
direct your users to the logout endpoint. A future release of ORY Kratos will
use POST Forms with Anti-CSRF Tokens to prevent this problem. This is tracked
as `}<a parentName="p" {...{
          "href": "https://github.com/ory/kratos/issues/142"
        }}>{`kratos#142`}</a>{`.`}</p>
    </blockquote>
    <p>{`To log a user out, all you have to do is to direct the browser to
`}<inlineCode parentName="p">{`http://ory-kratos-public/auth/browser/logout`}</inlineCode>{`. After successful logout, the
browser will be redirected to the `}<inlineCode parentName="p">{`redirect_to`}</inlineCode>{` value set in ORY Krato's
configuration file:`}</p>
    <pre><code parentName="pre" {...{}}>{`selfservice:
  logout:
    redirect_to: http://test.kratos.ory.sh:4000/
`}</code></pre>
    <h2 {...{
      "id": "self-service-user-logout-for-api-clients"
    }}>{`Self-Service User Logout for API Clients`}</h2>
    <p>{`This will be addressed in a future release of ORY Kratos.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
