export const metadata = {
  "id": "version-v0.2/index",
  "title": "Introduction",
  "description": "ORY Kratos is an API-first Identity and User Management system that is built",
  "source": "@site/versioned_docs/version-v0.2/index.md",
  "permalink": "/kratos/docs/index",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/versioned_docs/version-v0.2/index.md",
