  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index',
	title: 'Hooks'
};
export const metadata = {
  "id": "self-service/hooks/index",
  "title": "Hooks",
  "description": "Hooks execute logic before or after a flow (login, registration, settings, ...):",
  "source": "@site/docs/self-service/hooks/index.md",
  "permalink": "/kratos/docs/next/self-service/hooks/index",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/docs/self-service/hooks/index.md",
  "version": "next",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "docs",
  "previous": {
    "title": "Social Sign In with OpenID Connect and OAuth2",
    "permalink": "/kratos/docs/next/self-service/strategies/openid-connect-social-sign-in-oauth2"
  },
  "next": {
    "title": "Zero Trust with IAP Proxy",
    "permalink": "/kratos/docs/next/guides/zero-trust-iap-proxy-identity-access-proxy"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Login',
  id: 'login',
  children: [{
    value: 'Before',
    id: 'before',
    children: []
  }, {
    value: 'After',
    id: 'after',
    children: []
  }]
}, {
  value: 'Registration',
  id: 'registration',
  children: [{
    value: 'Before',
    id: 'before-1',
    children: []
  }, {
    value: 'After',
    id: 'after-1',
    children: []
  }, {
    value: '<code>verify</code>',
    id: 'verify',
    children: []
  }]
}, {
  value: 'Settings',
  id: 'settings',
  children: [{
    value: 'Before',
    id: 'before-2',
    children: []
  }, {
    value: 'After',
    id: 'after-2',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Hooks execute logic before or after a flow (login, registration, settings, ...):`}</p>
    <ul>
      <li parentName="ul"><em parentName="li">{`Before login:`}</em>{` is executed when a login is initiated.`}</li>
      <li parentName="ul"><em parentName="li">{`After login:`}</em>{` is executed after a login was successful.`}</li>
      <li parentName="ul"><em parentName="li">{`Before registration:`}</em>{` is executed when a registration is initiated.`}</li>
      <li parentName="ul"><em parentName="li">{`After registration:`}</em>{` is executed when a registration was successful:`}<ul parentName="li">
          <li parentName="ul"><em parentName="li">{`Before persisting:`}</em>{` runs before the identity is saved in the database.`}</li>
          <li parentName="ul"><em parentName="li">{`After persisting:`}</em>{` runs after the identity was saved in the database.`}</li>
        </ul></li>
      <li parentName="ul"><em parentName="li">{`Before settings:`}</em>{` is executed when a settings is initiated.`}</li>
      <li parentName="ul"><em parentName="li">{`After settings:`}</em>{` is executed when a settings was successful:`}<ul parentName="li">
          <li parentName="ul"><em parentName="li">{`Before persisting:`}</em>{` runs before the identity is saved in the database.`}</li>
          <li parentName="ul"><em parentName="li">{`After persisting:`}</em>{` runs after the identity was saved in the database.`}</li>
        </ul></li>
    </ul>
    <h2 {...{
      "id": "login"
    }}>{`Login`}</h2>
    <p>{`Hooks running before & after successful user authentication are defined per
Self-Service Login Strategy in ORY Kratos' configuration file.`}</p>
    <h3 {...{
      "id": "before"
    }}>{`Before`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    before:
      - hook: redirect
        config:
          to: https://www.ory.sh/maintenance
`}</code></pre>
    <h4 {...{
      "id": "redirect"
    }}><inlineCode parentName="h4">{`redirect`}</inlineCode></h4>
    <p>{`The `}<inlineCode parentName="p">{`redirect`}</inlineCode>{` job sends HTTP 302 Found and redirects the client
to the specified endpoint. This is useful
when you want to disable any settings functionality (e.g. due to maintenance).`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    before:
      - hook: redirect
        config:
          to: https://www.ory.sh/maintenance
`}</code></pre>
    <h3 {...{
      "id": "after"
    }}>{`After`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      oidc:
        - hook: redirect
          config:
            to: https://www.ory.sh/
      password:
        - hook: revoke_active_sessions
`}</code></pre>
    <h4 {...{
      "id": "redirect-1"
    }}><inlineCode parentName="h4">{`redirect`}</inlineCode></h4>
    <p>{`The `}<inlineCode parentName="p">{`redirect`}</inlineCode>{` job sends HTTP 302 Found and redirects the client
to the specified endpoint. This hook overrides the default redirection
behaviour and enforces the specified redirect URL.`}</p>
    <p>{`Using this hook should be an exception.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      <strategy>:
        - hook: redirect
          config:
            to: https://url-to-redirect/to
`}</code></pre>
    <h4 {...{
      "id": "revoke_active_sessions"
    }}><inlineCode parentName="h4">{`revoke_active_sessions`}</inlineCode></h4>
    <p>{`The `}<inlineCode parentName="p">{`revoke_active_sessions`}</inlineCode>{` will delete all active sessions for that user on
successful login:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      <strategy>:
        - hook: revoke_active_sessions
          # can not be configured
`}</code></pre>
    <h2 {...{
      "id": "registration"
    }}>{`Registration`}</h2>
    <p>{`Hooks running before & after successful user registration are defined per
Self-Service Registration Strategy in ORY Kratos' configuration file.`}</p>
    <h3 {...{
      "id": "before-1"
    }}>{`Before`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  registration:
    before:
    - hook: redirect
      config:
        to: https://www.ory.sh/maintenance
`}</code></pre>
    <h4 {...{
      "id": "redirect-2"
    }}><inlineCode parentName="h4">{`redirect`}</inlineCode></h4>
    <p>{`The `}<inlineCode parentName="p">{`redirect`}</inlineCode>{` job sends HTTP 302 Found and redirects the client
to the specified endpoint. This is useful
when you want to disable any settings functionality (e.g. due to maintenance).`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  registration:
    before:
    - hook: redirect
      config:
        to: https://www.ory.sh/maintenance
`}</code></pre>
    <h3 {...{
      "id": "after-1"
    }}>{`After`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  registration:
    after:
      oidc:
        - hook: session
      password:
        - hook: session
`}</code></pre>
    <h4 {...{
      "id": "session"
    }}><inlineCode parentName="h4">{`session`}</inlineCode></h4>
    <p>{`Adding the `}<inlineCode parentName="p">{`session`}</inlineCode>{` hook signs the user immediately in once the account has been created.
It runs after the identity has been saved to the database.`}</p>
    <p>{`::: warn
Using this job as part of your post-registration workflow makes your system
vulnerable to
`}<a parentName="p" {...{
        "href": "../../concepts/security.md#account-enumeration-attacks"
      }}>{`Account Enumeration Attacks`}</a>{`
because a threat agent can distinguish between existing and non-existing
accounts by checking if `}<inlineCode parentName="p">{`Set-Cookie`}</inlineCode>{` was sent as part of the registration
response.
:::`}</p>
    <p>{`It sends  a `}<inlineCode parentName="p">{`Set-Cookie`}</inlineCode>{` header which contains the session
cookie. To use it, you must first define one or more (for secret rotation)
session secrets and then use it in one of the `}<inlineCode parentName="p">{`after`}</inlineCode>{` work flows:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`secrets:
  session:
    - something-super-secret # The first entry will be used to sign and verify session cookies

    # All other entries will be used to verify session cookies that were signed before "something-super-secret" became
    # the current signing secret.
    - old-session-secret
    - older-session-secret
    - ancient-session-secret

selfservice:
  registration:
    after:
      <strategy>:
        - hook: session
          # can not be configured
`}</code></pre>
    <h4 {...{
      "id": "redirect-3"
    }}><inlineCode parentName="h4">{`redirect`}</inlineCode></h4>
    <p>{`The `}<inlineCode parentName="p">{`redirect`}</inlineCode>{` hook sends HTTP 302 Found and redirects the client
to the specified endpoint.`}</p>
    <p>{`::: Note
Using this hook for registration disables user registration because it runs
before the identity is saved to the database. It may
be useful in cases where you temporary suspend user registration.
:::`}</p>
    <p>{`Using this hook should be an exception.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  registration:
    after:
      <strategy>:
        - hook: redirect
          config:
            to: https://url-to-redirect/to
`}</code></pre>
    <h3 {...{
      "id": "verify"
    }}><inlineCode parentName="h3">{`verify`}</inlineCode></h3>
    <p>{`The `}<inlineCode parentName="p">{`verify`}</inlineCode>{` hook checks for verifiable email addresses and sends a verification / activation
email. For more information,
please read `}<a parentName="p" {...{
        "href": "/kratos/docs/next/self-service/flows/verify-email-account-activation"
      }}>{`User Verification and Account Activation`}</a>{`.`}</p>
    <h2 {...{
      "id": "settings"
    }}>{`Settings`}</h2>
    <p>{`Hooks running before & after successfully updating user settings and are defined per
Self-Service Settings Strategy in ORY Kratos' configuration file.`}</p>
    <h3 {...{
      "id": "before-2"
    }}>{`Before`}</h3>
    <p>{`Settings flows do not have before hooks.`}</p>
    <h3 {...{
      "id": "after-2"
    }}>{`After`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  settings:
    after:
      - hook: redirect
        config:
         to: https://www.ory.sh/
`}</code></pre>
    <h4 {...{
      "id": "redirect-4"
    }}><inlineCode parentName="h4">{`redirect`}</inlineCode></h4>
    <p>{`The `}<inlineCode parentName="p">{`redirect`}</inlineCode>{` job sends HTTP 302 Found and redirects the client
to the specified endpoint.`}</p>
    <p>{`Per default, the settings endpoint returns to the settings page
with the original settings request ID. This is useful
when you want to show e.g. a success message indicating
that the data has successfully been saved.`}</p>
    <p>{`To override this behaviour, use this redirect hook.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/my/kratos.config.yml\"",
        "title": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  settings:
    after:
      <strategy>:
        - hook: redirect
          config:
            to: https://www.ory.sh/settings-updated
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
