  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index',
	title: 'Overview'
};
export const metadata = {
  "id": "self-service/flows/index",
  "title": "Overview",
  "description": "ORY Kratos allows end-users to sign up, log in, log out, update profile",
  "source": "@site/docs/self-service/flows/index.md",
  "permalink": "/kratos/docs/next/self-service/flows/index",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/docs/self-service/flows/index.md",
  "version": "next",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "docs",
  "previous": {
    "title": "Before you start reading",
    "permalink": "/kratos/docs/next/self-service/index"
  },
  "next": {
    "title": "User Login and User Registration",
    "permalink": "/kratos/docs/next/self-service/flows/user-login-user-registration"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Network Flows for Browsers',
  id: 'network-flows-for-browsers',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`ORY Kratos allows end-users to sign up, log in, log out, update profile
information, recover accounts, and perform other important account management
tasks without third party involvement ("self-service").`}</p>
    <p>{`The opposite to self-service management is someone using administrative
privileges to create, update, or delete accounts.`}</p>
    <h2 {...{
      "id": "network-flows-for-browsers"
    }}>{`Network Flows for Browsers`}</h2>
    <p>{`All Self-Service Flows such as `}<a parentName="p" {...{
        "href": "./user-login.md"
      }}>{`User Login`}</a>{`,
`}<a parentName="p" {...{
        "href": "/kratos/docs/next/self-service/flows/user-login-user-registration"
      }}>{`User Registration`}</a>{`,
`}<a parentName="p" {...{
        "href": "./user-profile-management.md"
      }}>{`Profile Management`}</a>{` use the same template:`}</p>
    <ol>
      <li parentName="ol">{`The Browser makes an HTTP request to the flow's initialization endpoint (e.g.
`}<inlineCode parentName="li">{`/auth/browser/login`}</inlineCode>{`);`}</li>
      <li parentName="ol">{`The initialization endpoint processes data and associates it with a request
ID and redirects the browser to the flow's configured UI URL (e.g.
`}<inlineCode parentName="li">{`urls.login_ui`}</inlineCode>{`), appending the request ID as the `}<inlineCode parentName="li">{`request`}</inlineCode>{` URL Query
Parameter;`}</li>
      <li parentName="ol">{`The endpoint responsible for the UI URL uses the `}<inlineCode parentName="li">{`request`}</inlineCode>{` URL Query
Parameter (e.g. `}<inlineCode parentName="li">{`http://my-app/auth/login?request=abcde`}</inlineCode>{`) to fetch the data
previously associated with the Request ID from either ORY Kratos's Public or
Admin API.`}</li>
      <li parentName="ol">{`The UI endpoint renders the fetched data in any way it sees it fit. The flow
is typically completed by the browser making another request to one of ORY
Kratos' endpoints, which is usually described in the fetched request data.`}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
