  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'selfservice-flow-completion',
	title: 'Self-Service Flow Completion'
};
export const metadata = {
  "id": "version-v0.2/concepts/selfservice-flow-completion",
  "title": "Self-Service Flow Completion",
  "description": "Self-Service flows such as Login, Registration, Updating Settings support two",
  "source": "@site/versioned_docs/version-v0.2/concepts/selfservice-flow-completion.md",
  "permalink": "/kratos/docs/concepts/selfservice-flow-completion",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/versioned_docs/version-v0.2/concepts/selfservice-flow-completion.md",
  "version": "v0.2",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Redirection',
  id: 'redirection',
  children: []
}, {
  value: 'JSON',
  id: 'json',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Self-Service flows such as Login, Registration, Updating Settings support two
successful response modes:`}</p>
    <ul>
      <li parentName="ul">{`For browsers, the response will be a `}<a parentName="li" {...{
          "href": "#redirection"
        }}>{`redirection`}</a>{`.`}</li>
      <li parentName="ul">{`For API clients (this includes AJAX) the response will be in `}<a parentName="li" {...{
          "href": "#json"
        }}>{`JSON`}</a>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "redirection"
    }}>{`Redirection`}</h2>
    <p>{`Browser requests, identified by the `}<inlineCode parentName="p">{`Accept: text/html`}</inlineCode>{` header, complete with a
redirection flow. If no redirection URL is set for the flow, the Default
Redirect URL will be used:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`urls:
  default_redirect_to: https://always-end-up-here-per-default/
`}</code></pre>
    <p>{`It is possible to specify a redirect URL per Self-Service Flow:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      default_redirect_to: https://end-up-here-after-login/
  registration:
    after:
      default_redirect_to: https://end-up-here-after-registration/
  # ...
`}</code></pre>
    <p>{`You may also set redirect URLs per strategy (overrides
`}<inlineCode parentName="p">{`selfservice.<login|registration|...>.default_return_to`}</inlineCode>{`):`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      default_redirect_to: https://this-is-overridden-by-password/
      password:
        default_redirect_to: https://end-up-here-after-login-with-password/
    # ...
`}</code></pre>
    <p>{`It is also possible to redirect someone back to the original URL. For example,
if a user requests `}<inlineCode parentName="p">{`https://www.myapp.com/blog/write`}</inlineCode>{` but is not logged in, we
want the user to end up at that page after login. To achieve that, you append
`}<inlineCode parentName="p">{`?return_to=https://www.myapp.com/blog/write`}</inlineCode>{` when initializing the Login /
Registration /Settings flow.`}</p>
    <p>{`Because ORY Kratos prevents Open Redirect Attacks, you need to whitelist the
domain in your ORY Kratos config:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`urls:
  whitelisted_return_to_urls:
    - https://www.myapp.com/
`}</code></pre>
    <h2 {...{
      "id": "json"
    }}>{`JSON`}</h2>
    <p>{`This feature is currently in prototype phase and will be documented at a later
stage.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
