  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'user-settings-profile',
	title: 'User Profile'
};
export const metadata = {
  "id": "version-v0.2/self-service/strategies/user-settings-profile",
  "title": "User Profile",
  "description": "The `profile` strategy allows a user to change their identity traits",
  "source": "@site/versioned_docs/version-v0.2/self-service/strategies/user-settings-profile.md",
  "permalink": "/kratos/docs/self-service/strategies/user-settings-profile",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/versioned_docs/version-v0.2/self-service/strategies/user-settings-profile.md",
  "version": "v0.2",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "version-v0.2/docs",
  "previous": {
    "title": "Social Sign In with OpenID Connect and OAuth2",
    "permalink": "/kratos/docs/self-service/strategies/openid-connect-social-sign-in-oauth2"
  },
  "next": {
    "title": "Hooks",
    "permalink": "/kratos/docs/self-service/hooks/index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Browser Clients',
  id: 'browser-clients',
  children: []
}, {
  value: 'API Clients',
  id: 'api-clients',
  children: []
}, {
  value: 'Security',
  id: 'security',
  children: [{
    value: 'Account Enumeration Defenses (work in progress)',
    id: 'account-enumeration-defenses-work-in-progress',
    children: []
  }, {
    value: 'Account Takeover Defenses',
    id: 'account-takeover-defenses',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The `}<inlineCode parentName="p">{`profile`}</inlineCode>{` strategy allows a user to change their identity traits
("profile").`}</p>
    <p>{`The updated traits must be valid against the JSON Schema defined for its
`}<a parentName="p" {...{
        "href": "/kratos/docs/concepts/identity-user-model"
      }}>{`Identity Traits`}</a>{`. If one or more fields
do not validate (e.g. "Not an email"), the profile will not be updated.`}</p>
    <p>{`ORY Kratos will prompt the user to re-authenticate if a privileged trait (e.g.
email used to log in) is changed, similar to the
`}<a parentName="p" {...{
        "href": "https://help.github.com/en/github/authenticating-to-github/sudo-mode"
      }}>{`GitHub sudo mode`}</a>{`.`}</p>
    <p>{`You can configure how long a session is "privileged" by setting:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "title=\"path/to/kratos/config.yml\"",
        "title": "\"path/to/kratos/config.yml\""
      }}>{`selfservice:
  settings:
    # Sessions older than a minute requires the user to sign in again before
    # the password is changed.
    privileged_session_max_age: 1m
`}</code></pre>
    <p>{`This strategy does not implement any other flow!`}</p>
    <h2 {...{
      "id": "browser-clients"
    }}>{`Browser Clients`}</h2>
    <p>{`The Settings Request payload for this strategy looks as follows:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "id": "9c6473d4-9ffd-44cd-9794-adb82cbe4009",
  "expires_at": "2020-05-02T16:06:08.131598Z",
  "issued_at": "2020-05-02T15:06:08.131599Z",
  "request_url": "http://127.0.0.1:4433/self-service/browser/flows/settings",
  "active": "profile",
  "methods": {
    "profile": {
      "method": "profile",
      "config": {
        "action": "http://127.0.0.1:4455/.ory/kratos/public/self-service/browser/flows/settings/strategies/profile?request=9c6473d4-9ffd-44cd-9794-adb82cbe4009",
        "method": "POST",
        "fields": [
          {
            "name": "csrf_token",
            "type": "hidden",
            "required": true,
            "value": "HfvOBEv/D6PbXc89hsoEd0UMfZXKsvQXJzmXZJnq6BsBAwm8zbMKQF4LLZwRL67hPDlViB/qcJtu6yf+klIBeg=="
          },
          {
            "name": "traits.email",
            "type": "text",
            "required": false,
            "value": "xx0bdh@l7zkk8"
          },
          {
            "name": "traits.website",
            "type": "text",
            "required": false,
            "value": "http://github.com/aeneasr"
          }
        ]
      }
    }
  },
  "identity": {
    "id": "93548b1b-c8dc-4d3e-b19f-cfc6d812a8d0",
    "traits_schema_id": "default",
    "traits": {
      "email": "xx0bdh@l7zkk8",
      "website": "http://github.com/aeneasr"
    }
  },
  "update_successful": false
}
`}</code></pre>
    <p>{`If the form validation fails, an error will bei included:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json5"
      }}>{`{
  id: '9c6473d4-9ffd-44cd-9794-adb82cbe4009',
  // expires_at, ...
  active: 'profile',
  methods: {
    profile: {
      method: 'profile',
      config: {
        // action, ...
        fields: [
          // csrf_token, ...
          {
            name: 'traits.website',
            type: 'text',
            required: false,
            value: 'http://s',
            errors: [
              {
                message: 'length must be >= 10, but got 8',
              },
            ],
          },
        ],
      },
    },
  },
  // identity, ...
  update_successful: false,
}
`}</code></pre>
    <p>{`A successful flow will be marked with:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json5"
      }}>{`{
  id: '9c6473d4-9ffd-44cd-9794-adb82cbe4009',
  // expires_at, ...
  update_successful: true,
}
`}</code></pre>
    <h2 {...{
      "id": "api-clients"
    }}>{`API Clients`}</h2>
    <p>{`API-based login and registration using this strategy will be addressed in a
future release of ORY Kratos.`}</p>
    <h2 {...{
      "id": "security"
    }}>{`Security`}</h2>
    <h3 {...{
      "id": "account-enumeration-defenses-work-in-progress"
    }}>{`Account Enumeration Defenses (work in progress)`}</h3>
    <blockquote>
      <p parentName="blockquote">{`This feature is a work in progress and is tracked as
`}<a parentName="p" {...{
          "href": "https://github.com/ory/kratos/issues/133"
        }}>{`kratos#133`}</a>{`.`}</p>
    </blockquote>
    <h3 {...{
      "id": "account-takeover-defenses"
    }}>{`Account Takeover Defenses`}</h3>
    <p>{`The Settings flow implements account takeover defenses as it is not possible to
change the password without knowing the existing password. A good example of
this flow is the
`}<a parentName="p" {...{
        "href": "https://help.github.com/en/github/authenticating-to-github/sudo-mode"
      }}>{`GitHub sudo mode`}</a>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
