  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'sign-in-with-github',
	title: 'Sign in with GitHub'
};
export const metadata = {
  "id": "guides/sign-in-with-github",
  "title": "Sign in with GitHub",
  "description": "In this document we will take a look at setting up \"Sign in with GitHub\" using",
  "source": "@site/docs/guides/sign-in-with-github.mdx",
  "permalink": "/kratos/docs/next/guides/sign-in-with-github",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/docs/guides/sign-in-with-github.mdx",
