  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'selfservice-flow-completion',
	title: 'Self-Service Flow Completion'
};
export const metadata = {
  "id": "concepts/selfservice-flow-completion",
  "title": "Self-Service Flow Completion",
  "description": "Self-Service flows such as Login, Registration, Updating Settings support two",
  "source": "@site/docs/concepts/selfservice-flow-completion.md",
  "permalink": "/kratos/docs/next/concepts/selfservice-flow-completion",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/docs/concepts/selfservice-flow-completion.md",
  "version": "next",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "docs",
  "previous": {
    "title": "Social Sign In, OpenID Connect, and OAuth2",
    "permalink": "/kratos/docs/next/concepts/credentials/openid-connect-oidc-oauth2"
  },
  "next": {
    "title": "Out-of-band communication via E-Mail and SMS",
    "permalink": "/kratos/docs/next/concepts/email-sms"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Redirection',
  id: 'redirection',
  children: [{
    value: 'Post-Login Redirection',
    id: 'post-login-redirection',
    children: []
  }, {
    value: 'Post-Registration Redirection',
    id: 'post-registration-redirection',
    children: []
  }, {
    value: 'Post-Settings Redirection',
    id: 'post-settings-redirection',
    children: []
  }]
}, {
  value: 'JSON',
  id: 'json',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Self-Service flows such as Login, Registration, Updating Settings support two
successful response modes:`}</p>
    <ul>
      <li parentName="ul">{`For browsers, the response will be a `}<a parentName="li" {...{
          "href": "#redirection"
        }}>{`redirection`}</a>{`.`}</li>
      <li parentName="ul">{`For API clients (this includes AJAX) the response will be in `}<a parentName="li" {...{
          "href": "#json"
        }}>{`JSON`}</a>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "redirection"
    }}>{`Redirection`}</h2>
    <p>{`Browser requests, identified by the `}<inlineCode parentName="p">{`Accept: text/html`}</inlineCode>{` header, complete with a
redirection flow. If no redirection URL is set for the flow, the Default
Redirect URL will be used for most flows (e.g. login, registration):`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`urls:
  default_redirect_to: https://always-end-up-here-per-default/
`}</code></pre>
    <p>{`It is possible to specify a redirect URL per Self-Service Flow:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      default_redirect_to: https://end-up-here-after-login/
  registration:
    after:
      default_redirect_to: https://end-up-here-after-registration/
  # ...
`}</code></pre>
    <p>{`You may also set redirect URLs per strategy (overrides
`}<inlineCode parentName="p">{`selfservice.<login|registration|...>.default_return_to`}</inlineCode>{`):`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  login:
    after:
      default_redirect_to: https://this-is-overridden-by-password/
      password:
        default_redirect_to: https://end-up-here-after-login-with-password/
    # ...
`}</code></pre>
    <p>{`It is also possible to redirect someone back to the original URL. For example,
if a user requests `}<inlineCode parentName="p">{`https://www.myapp.com/blog/write`}</inlineCode>{` but is not logged in, we
want the user to end up at that page after login. To achieve that, you append
`}<inlineCode parentName="p">{`?return_to=https://www.myapp.com/blog/write`}</inlineCode>{` when initializing the Login /
Registration /Settings flow.`}</p>
    <p>{`Because ORY Kratos prevents Open Redirect Attacks, you need to whitelist the
domain in your ORY Kratos config:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`urls:
  whitelisted_return_to_urls:
    - https://www.myapp.com/
`}</code></pre>
    <h3 {...{
      "id": "post-login-redirection"
    }}>{`Post-Login Redirection`}</h3>
    <p>{`Post-login redirection considers the following configuration keys:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`urls:
  default_redirect_to: https://end-up-here-per-default/

selfservice:
  login:
    after:
      # overrides url.default_redirect_to
      default_redirect_to: https://this-is-overridden-by-password/
      password:
        # overrides selfservice.login.after.default_redirect_to
        default_redirect_to: https://end-up-here-after-login-with-password/
`}</code></pre>
    <h3 {...{
      "id": "post-registration-redirection"
    }}>{`Post-Registration Redirection`}</h3>
    <p>{`Post-login redirection considers the following configuration keys:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`urls:
  default_redirect_to: https://end-up-here-per-default/

selfservice:
  registration:
    after:
      # overrides url.default_redirect_to
      default_redirect_to: https://this-is-overridden-by-password/
      password:
        # overrides selfservice.registration.after.default_redirect_to
        default_redirect_to: https://end-up-here-after-registration-with-password/
`}</code></pre>
    <h3 {...{
      "id": "post-settings-redirection"
    }}>{`Post-Settings Redirection`}</h3>
    <p>{`Post-settings redirection `}<strong parentName="p">{`does not use`}</strong>{` the `}<inlineCode parentName="p">{`urls.default_redirect_to`}</inlineCode>{` configuration key. Instead
the redirect ends at the same Settings UI with the same Settings Request ID and key `}<inlineCode parentName="p">{`update_successful`}</inlineCode>{`
set to `}<inlineCode parentName="p">{`true`}</inlineCode>{`. If the listed keys are set, the redirection will end up at the specified values:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml",
        "metastring": "file=\"path/to/my/kratos.config.yml\"",
        "file": "\"path/to/my/kratos.config.yml\""
      }}>{`selfservice:
  settings:
    after:
      # overrides url.default_redirect_to
      default_redirect_to: https://this-is-overridden-by-password/
      password:
        # overrides selfservice.settings.after.default_redirect_to
        default_redirect_to: https://end-up-here-after-settings-with-password/
`}</code></pre>
    <h2 {...{
      "id": "json"
    }}>{`JSON`}</h2>
    <p>{`This feature is currently in prototype phase and will be documented at a later
stage.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
