  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index',
	title: 'Before you start reading'
};
export const metadata = {
  "id": "version-v0.2/self-service/index",
  "title": "Before you start reading",
  "description": "ORY Kratos implements flows that users perform themselves as opposed to",
  "source": "@site/versioned_docs/version-v0.2/self-service/index.md",
  "permalink": "/kratos/docs/self-service/index",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/versioned_docs/version-v0.2/self-service/index.md",
