  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {};
export const metadata = {
  "id": "version-v0.1/self-service/flows/user-profile-management",
  "title": "user-profile-management",
  "description": "ORY Kratos allows users to update their own profile information using two",
  "source": "@site/versioned_docs/version-v0.1/self-service/flows/user-profile-management.md",
  "permalink": "/kratos/docs/v0.1/self-service/flows/user-profile-management",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/versioned_docs/version-v0.1/self-service/flows/user-profile-management.md",
  "version": "v0.1",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "version-v0.1/docs",
  "previous": {
    "title": "User Logout",
    "permalink": "/kratos/docs/v0.1/self-service/flows/user-logout"
  },
  "next": {
    "title": "Account Recovery",
    "permalink": "/kratos/docs/v0.1/self-service/flows/password-reset-account-recovery"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Self-Service User Profile Management for Browser Applications',
  id: 'self-service-user-profile-management-for-browser-applications',
  children: [{
    value: 'Server-Side Browser Applications',
    id: 'server-side-browser-applications',
    children: []
  }, {
    value: 'Client-Side Browser Applications',
    id: 'client-side-browser-applications',
    children: []
  }]
}, {
  value: 'Self-Service User Profile Management for API Clients',
  id: 'self-service-user-profile-management-for-api-clients',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`ORY Kratos allows users to update their own profile information using two
principal flows:`}</p>
    <ul>
      <li parentName="ul">{`Browser-based (easy): This flow works for all applications running on top of a
browser. Websites, single-page apps, Cordova/Ionic, and so on.`}</li>
      <li parentName="ul">{`API-based (advanced): This flow works for native applications like iOS
(Swift), Android (Java), Microsoft (.NET), React Native, Electron, and others.`}</li>
    </ul>
    <p>{`This flow does not allow updates of security-sensitive information such as the
password, fields associated with login (e.g. email), fields associated with
account recovery (e.g. recovery email address). These fields must be updated
using a separate flow which requires prior security checks.`}</p>
    <p>{`The updated profile must be valid against the JSON Schema defined for its
`}<a parentName="p" {...{
        "href": "/kratos/docs/v0.1/concepts/identity-user-model"
      }}>{`Identity Traits`}</a>{`. If one or more fields
do not validate (e.g. "Not an email"), the profile will not be updated.`}</p>
    <p>{`The only required configuration is setting the Profile UI URL in the
`}<a parentName="p" {...{
        "href": "/kratos/docs/v0.1/reference/configuration"
      }}>{`ORY Kratos configuration file`}</a>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`urls:
  profile_ui: https://.../..
`}</code></pre>
    <h2 {...{
      "id": "self-service-user-profile-management-for-browser-applications"
    }}>{`Self-Service User Profile Management for Browser Applications`}</h2>
    <p>{`This flow is similar to
`}<a parentName="p" {...{
        "href": "user-login-user-registration.mdx"
      }}>{`User Login and User Registration`}</a>{` but does not
support before/after work flows or individual strategies. It uses the already
established `}<a parentName="p" {...{
        "href": "/kratos/docs/v0.1/self-service/flows/index#network-flows-for-browsers"
      }}>{`Network Flows for Browsers`}</a>{`.`}</p>
    <h3 {...{
      "id": "server-side-browser-applications"
    }}>{`Server-Side Browser Applications`}</h3>
    <p>{`The `}<a parentName="p" {...{
        "href": "/kratos/docs/v0.1/self-service/flows/index#network-flows-for-browsers"
      }}>{`Network Flows for Browsers`}</a>{` works as
follows for Profile Management:`}</p>
    <ol>
      <li parentName="ol">{`An initial HTTP Request is made to
`}<inlineCode parentName="li">{`https://example.org/.ory/kratos/public/self-service/browser/flows/profile`}</inlineCode>{`.`}</li>
      <li parentName="ol">{`ORY Kratos redirects the browser to the URL set in `}<inlineCode parentName="li">{`urls.profile_ui`}</inlineCode>{` and
appends the `}<inlineCode parentName="li">{`request`}</inlineCode>{` URL Query Parameter (e.g.
`}<inlineCode parentName="li">{`https://example.org/profile?request=abcde`}</inlineCode>{`).`}</li>
      <li parentName="ol">{`The Endpoint at `}<inlineCode parentName="li">{`/profile`}</inlineCode>{` makes a HTTP GET Request to
`}<inlineCode parentName="li">{`https://ory-kratos-admin.example-org.vpc/self-service/browser/flows/requests/profile?request=abcde`}</inlineCode>{`
and fetches Profile Management Request JSON Payload that represent the
individual fields that can be updated.`}</li>
      <li parentName="ol">{`The User updates the profile data and sends a HTTP POST request to
`}<inlineCode parentName="li">{`https://example.org/.ory/kratos/public/self-service/browser/flows/profile/update?request=abcde`}</inlineCode>{`.`}<ul parentName="li">
          <li parentName="ul">{`If the profile data is invalid, all validation errors will be collected and
added to the Profile Management JSON Payload. The Browser is redirected to
the `}<inlineCode parentName="li">{`urls.profile_ui`}</inlineCode>{` URL (e.g.
`}<inlineCode parentName="li">{`https://example.org/profile?request=abcde`}</inlineCode>{`).`}</li>
          <li parentName="ul">{`If the profile data is valid, the identity's traits are updated and the
process is complete.`}</li>
        </ul></li>
    </ol>
    <p>{`Assuming the Identity's Traits JSON Schema is defined as`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "$id": "https://example.org/identity.traits.schema.json",
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Person",
  "type": "object",
  "properties": {
    "email": {
      "type": "string",
      "format": "email",
      "title": "E-Mail",
      "minLength": 3,
      "ory.sh/kratos": {
        "credentials": {
          "password": {
            "identifier": true
          }
        }
      }
    },
    "name": {
      "type": "object",
      "properties": {
        "first": {
          "type": "string",
          "minLength": 5
        },
        "last": {
          "type": "string"
        }
      }
    }
  },
  "required": ["email"],
  "additionalProperties": false
}
`}</code></pre>
    <p>{`the resulting JSON Payload coming from
`}<inlineCode parentName="p">{`https://ory-kratos-admin.example-org.vpc/self-service/browser/flows/requests/profile?request=abcde`}</inlineCode>{`
would look something along the lines of:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "id": "48068b5d-3438-4d6f-9955-331b96c41762",
  "expires_at": "2020-01-27T23:03:58.5986947Z",
  "issued_at": "2020-01-27T22:03:58.5987099Z",
  "request_url": "http://127.0.0.1:4455/profiles",
  "form": {
    "action": "https://example.org/.ory/kratos/public/profiles?48068b5d-3438-4d6f-9955-331b96c41762",
    "method": "POST",
    "fields": {
      "csrf_token": {
        "name": "csrf_token",
        "type": "hidden",
        "required": true,
        "value": "+5+WxP7+leOpfjHHkWWe99APD7845i82p4wGtfdWKHBK5fFg4BS7JjzdeI7kdsOUElyrG10ZR5vIqi7asNpqAA=="
      },
      "traits.email": {
        "name": "traits.email",
        "type": "text",
        "value": "foobar@ory.sh"
      },
      "traits.name.first": {
        "name": "traits.name.first",
        "type": "text",
        "value": "Foobar"
      },
      "traits.name.last": {
        "name": "traits.name.last",
        "type": "text",
        "value": "Barbaz"
      }
    }
  },
  "identity": {
    "id": "c631e58a-445a-4844-ae80-f0b426a1e11e",
    "traits_schema_id": "default",
    "traits_schema_url": "https://example.org/identity.traits.schema.json",
    "traits": {
      "email": "foobar@ory.sh",
      "name": {
        "last": "Foobar",
        "first": "Barbaz"
      }
    }
  }
}
`}</code></pre>
    <p>{`If the user tries to save profile data that does not validate against the
provided JSON Schema, error payloads will be added to the fields affected:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json5"
      }}>{`{
  id: '48068b5d-3438-4d6f-9955-331b96c41762',
  // ...
  form: {
    // ...
    fields: {
      // ...
      'traits.name.first': {
        name: 'traits.name.first',
        type: 'text',
        value: 'abc',
        errors: [
          {
            message: 'traits.name.first: Must be at least 5 characters long',
          },
        ],
      },
      // ...
    },
  },
  // ...
}
`}</code></pre>
    <p>{`Keep in mind that it is not possible to update the `}<inlineCode parentName="p">{`traits.email`}</inlineCode>{` field as
updating that field requires prior authentication.`}</p>
    <blockquote>
      <p parentName="blockquote">{`Updating these "protected" fields will be implemented in a future release of
ORY Kratos.`}</p>
    </blockquote>
    <h3 {...{
      "id": "client-side-browser-applications"
    }}>{`Client-Side Browser Applications`}</h3>
    <p>{`Because Client-Side Browser Applications do not have access to ORY Kratos' Admin
API, they must use the ORY Kratos Public API instead. The flow for a Client-Side
Browser Application is almost the exact same as the one for Server-Side
Applications, with the small difference that
`}<inlineCode parentName="p">{`https://example.org/.ory/kratos/public/self-service/browser/flows/requests/profile?request=abcde`}</inlineCode>{`
would be called via AJAX instead of making a request to
`}<inlineCode parentName="p">{`https://ory-kratos-admin.example-org.vpc/self-service/browser/flows/requests/profile?request=abcde`}</inlineCode>{`.`}</p>
    <blockquote>
      <p parentName="blockquote">{`To prevent brute force, guessing, session injection, and other attacks, it is
required that cookies are working for this endpoint. The cookie set in the
initial HTTP request made to `}<inlineCode parentName="p">{`https://example.org/.ory/kratos/public/profiles`}</inlineCode>{`
MUST be set and available when calling this endpoint!`}</p>
    </blockquote>
    <h2 {...{
      "id": "self-service-user-profile-management-for-api-clients"
    }}>{`Self-Service User Profile Management for API Clients`}</h2>
    <p>{`Will be addressed in a future release.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
