export const rightToc = [{
  value: 'User-Facing Errors in the Browser',
  id: 'user-facing-errors-in-the-browser',
  children: []
}, {
  value: 'User-Facing Errors when consuming APIs',
  id: 'user-facing-errors-when-consuming-apis',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Because ORY Kratos does not render User Interfaces, we implemented a flow that
allows you to implement the error page in any way you want.`}</p>
    <h2 {...{
      "id": "user-facing-errors-in-the-browser"
    }}>{`User-Facing Errors in the Browser`}</h2>
    <p>{`When a user-facing error occurs (e.g. during Self Service User Login), ORY
Kratos will store error message and context and redirect the User's Browser to
the Error UI URL set by the `}<inlineCode parentName="p">{`urls.error_ui`}</inlineCode>{` configuration or `}<inlineCode parentName="p">{`URLS_ERROR_UI`}</inlineCode>{`
environment variable.`}</p>
    <p>{`Assuming `}<inlineCode parentName="p">{`urls.error_ui`}</inlineCode>{` is set to `}<inlineCode parentName="p">{`https://example.org/errors`}</inlineCode>{`, ORY Kratos will
redirect the User's Browser to `}<inlineCode parentName="p">{`https://example.org/errors?error=abcde`}</inlineCode>{`.`}</p>
    <p>{`The route matching `}<inlineCode parentName="p">{`https://example.org/errors`}</inlineCode>{` uses the `}<inlineCode parentName="p">{`error`}</inlineCode>{` URL Query
parameter value `}<inlineCode parentName="p">{`abcde`}</inlineCode>{` to make a request to ORY Kratos' Public or Admin API
`}<inlineCode parentName="p">{`https://kratos-<public|admin/self-service/errors?error=abcde`}</inlineCode>{`. The JSON
Response contains a list of errors and their details, for example:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`[
  {
    "code": 500,
    "message": "no such file or directory"
  }
]
`}</code></pre>
    <p>{`We are working on documenting possible error messages and homogenize error
layouts (tracked as ) In general, errors have the following keys defined:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "code": 500,
  "message": "some message",
  "reason": "some reason",
  "debug": "some debug info"
}
`}</code></pre>
    <h2 {...{
      "id": "user-facing-errors-when-consuming-apis"
    }}>{`User-Facing Errors when consuming APIs`}</h2>
    <p>{`When a user-facing error occurs and the HTTP client is an API Client (e.g.
Mobile App), the error will be returned as the HTTP Response. No additional
steps are required.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
