export const rightToc = [{
  value: 'Browser Clients',
  id: 'browser-clients',
  children: [{
    value: 'Login',
    id: 'login',
    children: []
  }, {
    value: 'Registration',
    id: 'registration',
    children: []
  }]
}, {
  value: 'API Clients',
  id: 'api-clients',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <blockquote>
      <p parentName="blockquote">{`The OpenID Connect Strategy does already work but needs further documentation
and code improvements. It will be documented in an upcoming release of ORY
Kratos.`}</p>
    </blockquote>
    <p>{`The Social Sign In Strategy enables you to use`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "http://github.com/"
        }}>{`GitHub`}</a>{`;`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.apple.com/sign-in-with-apple/"
        }}>{`Apple`}</a>{`;`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developers.google.com/identity/sign-in/web/sign-in"
        }}>{`Google`}</a>{`;`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developers.facebook.com/docs/facebook-login/"
        }}>{`Facebook`}</a>{`;`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.ory.sh/hydra"
        }}>{`ORY Hydra`}</a>{`;`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.keycloak.org"
        }}>{`Keycloak`}</a>{`;`}</li>
      <li parentName="ul">{`and every other OpenID Connect Certified Provider`}</li>
    </ul>
    <p>{`as the Identity Provider.`}</p>
    <p>{`Because of the nature of this flow (a browser is required) it does not work`}</p>
    <h2 {...{
      "id": "browser-clients"
    }}>{`Browser Clients`}</h2>
    <h3 {...{
      "id": "login"
    }}>{`Login`}</h3>
    <p>{`Sign In only works when an identity exists for that profile already. If it does
not exist, a registration flow will be performed instead.`}</p>
    <h3 {...{
      "id": "registration"
    }}>{`Registration`}</h3>
    <p>{`Sign Up on conflict with existing primary identifiers like email:`}</p>
    <ul>
      <li parentName="ul">{`Sign Up is dis-allowed and the user is asked to instead log in and then link
his/her account instead.`}</li>
    </ul>
    <h2 {...{
      "id": "api-clients"
    }}>{`API Clients`}</h2>
    <p>{`API-based login and registration using this strategy will be addressed in a
future release of ORY Kratos.`}</p>
    <p>{`Please be aware that OpenID Connect providers always require a Browser, with the
exception of "Sign in with Apple" on recent iOS versions.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
