  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'sign-in-with-github-google-facebook-linkedin',
	title: 'Sign in with GitHub, Google, Facebook, LinkedIn, ...'
};
export const metadata = {
  "id": "guides/sign-in-with-github-google-facebook-linkedin",
  "title": "Sign in with GitHub, Google, Facebook, LinkedIn, ...",
  "description": "In this document we will take a look at setting up \"Sign in with GitHub\" using",
  "source": "@site/docs/guides/sign-in-with-github-google-facebook-linkedin.mdx",
  "permalink": "/kratos/docs/next/guides/sign-in-with-github-google-facebook-linkedin",
  "editUrl": "https://github.com/ory/kratos/edit/master/docs/docs/guides/sign-in-with-github-google-facebook-linkedin.mdx",
